const TZ = 'America/New_York';
const DAILY_CAP_STAGE = 20;
const SEND_MINUTE_BLOCK = new Set([0,15,30,45]);
const BASE_SEND_RATE = 1;

function nowEtParts() {
  const parts = new Intl.DateTimeFormat('en-CA', {
    timeZone: TZ, weekday: 'short', hour12: false,
    year:'numeric', month:'2-digit', day:'2-digit',
    hour:'2-digit', minute:'2-digit', second:'2-digit',
  }).formatToParts(new Date());
  const m = Object.fromEntries(parts.map(p => [p.type, p.value]));
  const wd = ['sun','mon','tue','wed','thu','fri','sat']
    .indexOf(m.weekday.toLowerCase().slice(0,3));
  return { wd, h: +m.hour, mi: +m.minute, date: `${m.year}-${m.month}-${m.day}` };
}
function inEtWindow(h, mi) {
  const t = h*60 + mi;
  const amStart = 9*60, amEnd = 11*60 + 59;
  const pmStart = 13*60, pmEnd = 16*60 + 59;
  return (t>=amStart && t<=amEnd) || (t>=pmStart && t<=pmEnd);
}

const g = $getWorkflowStaticData('global');
g._outreachDaily = g._outreachDaily || {};
const { wd, h, mi, date } = nowEtParts();
if (!g._outreachDaily.dateEt || g._outreachDaily.dateEt !== date) {
  g._outreachDaily = { dateEt: date, OR1: 0, OR2: 0, OR3: 0 };
}

if (wd===0 || wd===6) return [];
if (!inEtWindow(h, mi)) return [];
if (SEND_MINUTE_BLOCK.has(mi)) return [];

const { OR1, OR2, OR3 } = g._outreachDaily;
const remaining = { OR1: Math.max(0, DAILY_CAP_STAGE-OR1), OR2: Math.max(0, DAILY_CAP_STAGE-OR2), OR3: Math.max(0, DAILY_CAP_STAGE-OR3) };
if ((remaining.OR1 + remaining.OR2 + remaining.OR3) === 0) return [];

if (Math.random() >= BASE_SEND_RATE) return [];

return [{ json: { startedAt: new Date().toISOString(), remaining } }];

function truthy(v){ return v===true || v==='true' || v==='TRUE' || v===1 || v==='1'; }
function isYes(v){ return (v||'').toString().trim().toLowerCase()==='yes'; }
const out=[];
for (const item of items){
  const j=item.json||{};
  const trig=j['FollowUp Trigger'];
  const or2 = j['OR2 Sent'];
  const or3 = j['OR3 Sent'];
  const email = j['Primary Email'];
  if (!email) continue;
  if (isYes(trig)) continue;
  if (truthy(or2) || truthy(or3)) continue;
  out.push(item);
}
return out;

